import React, { useState, useEffect } from 'react';
import './css/Home.css';

const Home = () => {
  const [appointments, setAppointments] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);

  const handleAdminLogin = () => {
    const password = prompt("Enter admin password:");
    if (password === "krishna123") {
      setIsAdmin(true);
    } else {
      alert("Incorrect password!");
    }
  };

  useEffect(() => {
    if (isAdmin) {
      fetch('http://localhost:5000/api/appointments')
        .then((res) => res.json())
        .then((data) => setAppointments(data));
    }
  }, [isAdmin]);

  return (
    <section id="home" className="home-section">
      <div className="hero">
        <h2>Welcome to Krishna Clinic</h2>
        <p>General and Family Healthcare near Bhakti Circle, Ahmedabad</p>
        <a href="#appointment" className="btn">Book Appointment</a>
      </div>

      {/* Admin Panel */}
      {isAdmin ? (
        <div style={{ marginTop: '30px' }}>
          <h2>Admin: Appointment Requests</h2>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Mobile</th>
                <th>Reason</th>
                <th>Date/Time</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((appt) => (
                <tr key={appt.id}>
                  <td>{appt.full_name}</td>
                  <td>{appt.mobile}</td>
                  <td>{appt.reason}</td>
                  <td>{new Date(appt.datetime).toLocaleString('en-IN', {
                    dateStyle: 'medium',
                    timeStyle: 'short'
                  })}</td>
                  <td>{appt.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <button onClick={handleAdminLogin} style={{ padding: '10px 20px', background: '#007bff', color: 'white', border: 'none', borderRadius: '8px' }}>
            ðŸ”’ Admin Login
          </button>
        </div>
      )}
    </section>
  );
};

export default Home;
