// src/pages/AdminAppointments.js
import React, { useEffect, useState } from 'react';

const API_BASE_URL =
  process.env.NODE_ENV === 'production'
    ? 'https://your-live-backend-url.com' // üîÅ Replace with real backend
    : 'http://localhost:5000';

const AdminAppointments = () => {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    fetch(`${API_BASE_URL}/api/appointments`)
      .then(res => res.json())
      .then(data => setAppointments(data))
      .catch(err => console.error('Failed to fetch appointments:', err));
  }, []);

  const updateStatus = async (id, newStatus) => {
    try {
      const res = await fetch(`${API_BASE_URL}/api/appointments/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus })
      });

      if (res.ok) {
        setAppointments(prev =>
          prev.map(app =>
            app.id === id ? { ...app, status: newStatus } : app
          )
        );
      } else {
        alert('Failed to update status');
      }
    } catch (error) {
      console.error('Update error:', error);
    }
  };

  return (
    <section className="admin-appointments">
      <div className="container">
        <h2>Admin: Appointment Requests</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th><th>Mobile</th><th>Reason</th><th>Date/Time</th><th>Status</th><th>Action</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map(app => (
              <tr key={app.id}>
                <td>{app.full_name}</td>
                <td>{app.mobile}</td>
                <td>{app.reason}</td>
                <td>{app.datetime}</td>
                <td>{app.status}</td>
                <td>
                  <button onClick={() => updateStatus(app.id, 'confirmed')}>‚úÖ Confirm</button>
                  <button onClick={() => updateStatus(app.id, 'rejected')}>‚ùå Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </section>
  );
};

export default AdminAppointments;
