import React from 'react';
import { useLanguage } from '../context/LanguageContext';
import './Header.css';
import logo from '../assets/images/logo.png.jpg'; // ✅ Use correct path and file extension

const Header = () => {
  const { language, toggleLanguage } = useLanguage();

  return (
    <header className="clinic-header">
      <div className="top-bar">
        <div className="header-left">
          <img src={logo} alt="Krishna Clinic Logo" className="clinic-logo" />
          <div className="clinic-name">
            <h1>Krishna Clinic</h1>
            <p className="tagline">
              {language === 'en'
                ? 'Your health is our concern.'
                : 'તમારું સ્વાસ્થ્ય અમારી ચિંતા છે.'}
            </p>
          </div>
        </div>

        <div className="header-right">
          <p><strong>Dr. Kashyap Solanki</strong></p>
          <p>B.A.M.S. GAU</p>
          <p>Reg No: GB-I 22354</p>
          <p>Ex. Sarthak Medical & ICU Hospital – Cura Hospitals</p>
        </div>
      </div>


<nav className="nav-bar">
  <ul className="nav-links">
    <li><Link to="/">{language === 'en' ? 'Home' : 'હોમ'}</Link></li>
    <li><Link to="/about">{language === 'en' ? 'About' : 'વિશે'}</Link></li>
    <li><Link to="/services">{language === 'en' ? 'Services' : 'સેવામાં'}</Link></li>
    <li><Link to="/appointmentform">{language === 'en' ? 'Appointment' : 'અપોઇન્ટમેન્ટ'}</Link></li>
    <li><Link to="/contact">{language === 'en' ? 'Contact' : 'સંપર્ક'}</Link></li>
  </ul>
</nav>

    </header>
  );
};

export default Header;
