  const navigate = useNavigate(); // ‚úÖ MUST be inside the component

  const handleBookClick = () => {
    navigate('/appointmentform'); // ‚úÖ navigate is defined here
  };
  const todayIndex = new Date().getDay(); // 0 = Sunday

  const hours = [
    { day: 'Sunday (Ashura)', time: '9‚ÄØam‚Äì1‚ÄØpm, 4‚Äì9‚ÄØpm', holiday: true },
    { day: 'Monday', time: '9‚ÄØam‚Äì1‚ÄØpm, 4‚Äì9‚ÄØpm' },
